// *** Code Node: Verarbeitet alle Items vom Merge Node OHNE Set Nodes ***
// Mode: Run Once for All Items
// Quelle wird automatisch basierend auf URL erkannt

const items = $input.all();
const maxAge = 30 * 24 * 60 * 60 * 1000; // 30 Tage
const now = Date.now();

const aiKeywords = [
  'ai', 'artificial intelligence', 'machine learning', 'llm', 'gemini', 'gpt', 'claude',
  'automation', 'workflow', 'neural', 'deep learning', 'ki', 'kuenstliche intelligenz',
  'mittelstand', 'kmu', 'sme', 'digitalisierung', 'industrie 4.0', 'prozessautomatisierung',
  'rpa', 'robot process automation', 'chatbot', 'assistenzsystem', 'assistenzsysteme',
  'datenanalyse', 'predictive analytics', 'computer vision', 'nlp', 'natural language processing'
];

// Helper: Erkenne Source und Language basierend auf URL
function detectSourceAndLanguage(url) {
  if (!url) return { source: 'Unknown', language: 'en' };

  const urlLower = url.toLowerCase();

  // Google AI
  if (urlLower.includes('research.google.com') || urlLower.includes('ai.googleblog.com') || urlLower.includes('deepmind.google')) {
    return { source: 'Google AI', language: 'en' };
  }

  // OpenAI
  if (urlLower.includes('openai.com')) {
    return { source: 'OpenAI', language: 'en' };
  }

  // Anthropic
  if (urlLower.includes('anthropic.com') || urlLower.includes('reddit.com/r/anthropicai')) {
    return { source: 'Anthropic', language: 'en' };
  }

  // Hugging Face
  if (urlLower.includes('huggingface.co')) {
    return { source: 'Hugging Face', language: 'en' };
  }

  // n8n Blog
  if (urlLower.includes('reddit.com/r/n8n')) {
    return { source: 'n8n Blog', language: 'en' };
  }

  // The Decoder (DE)
  if (urlLower.includes('the-decoder.de')) {
    return { source: 'The Decoder', language: 'de' };
  }

  // TechCrunch AI
  if (urlLower.includes('techcrunch.com')) {
    return { source: 'TechCrunch AI', language: 'en' };
  }

  return { source: 'Unknown', language: 'en' };
}

// Helper f√ºr XML-Parsing
function normalizeArticle(article, type) {
  let title = '', link = '', description = '', pubDate = '';
  try {
    if (type === 'rss') {
      title = article.title?.[0]?._ || article.title?.[0] || article.title || '';
      link = article.link?.[0]?._ || article.link?.[0] || article.link || '';
      description = (article.description?.[0]?._ || article.description?.[0] || article.description || '').replace(/<[^>]*>/g, '').substring(0, 200);
      pubDate = article.pubDate?.[0]?._ || article.pubDate?.[0] || article.pubDate || new Date().toISOString();
    } else if (type === 'atom') {
      title = article.title?.[0]?._ || article.title?.[0] || article.title || '';
      link = (article.link?.[0]?.$?.href) || article.link?.[0]?._ || article.link?.[0] || article.link || '';
      description = (article.summary?.[0]?._ || article.summary?.[0] || article.content?.[0]?._ || article.content?.[0] || '').replace(/<[^>]*>/g, '').substring(0, 200);
      pubDate = article.published?.[0]?._ || article.published?.[0] || article.updated?.[0]?._ || article.updated?.[0] || new Date().toISOString();
    }
    return { title, link, description, pubDate };
  } catch (e) {
    return null;
  }
}

// Verarbeite alle Items vom Merge Node
const articles = []; // Alle Artikel (mit title/link)

console.log('üì• Total Items vom Merge:', items.length);
console.log('üìã Erste 3 Items:', items.slice(0, 3).map(i => Object.keys(i.json)));

for (const item of items) {
  const entry = item.json;

  // Wenn es ein RSS Feed Read Artikel ist (HAT direkt title + link)
  if (entry.title && entry.link) {
    const { source, language } = detectSourceAndLanguage(entry.link);

    articles.push({
      title: entry.title,
      link: entry.link,
      description: (entry.contentSnippet || entry.content || entry.description || '').replace(/<[^>]*>/g, '').substring(0, 200),
      pubDate: entry.isoDate || entry.pubDate || entry.date || new Date().toISOString(),
      source: source,
      language: language
    });
  }

  // Wenn es HTTP Request XML ist (rss/feed)
  if (entry.rss || entry.feed) {
    let rawArticles = [];
    let feedType = '';

    if (entry.rss?.channel?.[0]?.item) {
      rawArticles = entry.rss.channel[0].item;
      feedType = 'rss';
    } else if (entry.feed?.entry) {
      rawArticles = Array.isArray(entry.feed.entry) ? entry.feed.entry : [entry.feed.entry];
      feedType = 'atom';
    }

    for (const article of rawArticles) {
      const norm = normalizeArticle(article, feedType);
      if (norm && norm.title && norm.link) {
        const { source, language } = detectSourceAndLanguage(norm.link);

        articles.push({
          ...norm,
          source: source,
          language: language
        });
      }
    }
  }
}

console.log('üì∞ Artikel gefunden:', articles.length);
console.log('üìã Erste 3 Artikel:', articles.slice(0, 3).map(a => ({ title: a.title?.substring(0, 30), source: a.source })));

// Filterung und Kategorisierung
const filteredItems = [];

for (const article of articles) {
  if (!article.title || !article.link) continue;

  const titleLower = article.title.toLowerCase();
  const descLower = (article.description || '').toLowerCase();

  const isRelevant = aiKeywords.some(keyword => titleLower.includes(keyword) || descLower.includes(keyword));

  if (!isRelevant) continue;

  let category = 'gro√üe-modelle';
  if (titleLower.includes('workflow') || titleLower.includes('n8n') || titleLower.includes('automation') || titleLower.includes('prozessautomatisierung')) {
    category = 'workflow-tools';
  } else if (titleLower.includes('sales') || titleLower.includes('hubspot') || titleLower.includes('crm') || titleLower.includes('vertrieb')) {
    category = 'sales-tools';
  } else if (titleLower.includes('mittelstand') || titleLower.includes('kmu') || titleLower.includes('sme') || titleLower.includes('unternehmen')) {
    category = 'kmu-relevanz';
  } else if (titleLower.includes('industrie') || titleLower.includes('produktion') || titleLower.includes('manufacturing')) {
    category = 'industrie-4.0';
  } else if (titleLower.includes('dienstleistung') || titleLower.includes('service') || titleLower.includes('beratung')) {
    category = 'dienstleister-tools';
  }

  const itemDate = article.pubDate ? new Date(article.pubDate) : new Date();
  const age = now - itemDate.getTime();

  if (age <= maxAge && age >= 0) {
    filteredItems.push({
      title: article.title.trim(),
      description: article.description.trim(),
      link: article.link.trim(),
      date: itemDate.toISOString(),
      source: article.source || 'Unknown',
      category: category,
      language: article.language || 'en'
    });
  }
}

console.log('‚úÖ Nach Filterung:', filteredItems.length, 'Items');

// Entferne Duplikate (nach Link)
const uniqueItems = filteredItems.filter((item, index, self) =>
  index === self.findIndex(t => t.link === item.link)
);

// Sortiere nach Datum (neueste zuerst) und limitiere auf 15 News
const sortedItems = uniqueItems.sort((a, b) => new Date(b.date) - new Date(a.date)).slice(0, 15);

console.log('üì§ Finale Output:', sortedItems.length, 'Items');

// Return muss Array sein mit json-Property
if (sortedItems.length === 0) {
  // FALLBACK: Return leeres Array wenn keine Items (verhindert Workflow-Stop)
  return [{ json: { title: 'No articles found', link: '', description: '', date: new Date().toISOString(), source: 'System', category: 'system', language: 'en' } }];
}

return sortedItems.map(item => ({ json: item }));
