// --- Code f√ºr Setze Sample-Info (FINAL - CRASH-PROOF) ---

const sample = String($json.query?.sample || '').trim();

let binaryData = null;

// 1. $binary vom aktuellen Item
if ($binary && typeof $binary === 'object') {
    const keys = Object.keys($binary);
    if (keys.length > 0) {
        binaryData = $binary;
    }
}

// 2. Sample: Binary von "Lade Sample X" Node
if (!binaryData && sample) {
    try {
        const nodeName = sample === '1' ? 'Lade Sample 1' : sample === '2' ? 'Lade Sample 2' : 'Lade Sample 3';
        const node = $(nodeName);

        if (node?.binary) binaryData = node.binary;
        else if (node?.item?.binary) binaryData = node.item.binary;
        else if (node?.item?.binary?.data) binaryData = node.item.binary;
        else if (node?.item?.binary?.file) binaryData = { data: node.item.binary.file };
    } catch (e) {
        console.error('Fehler beim Holen von Lade Sample:', e);
    }
}

// 3. Upload: Binary vom Webhook
if (!binaryData && !sample) {
    try {
        const webhook = $('Business Card Upload');
        if (webhook?.binary) binaryData = webhook.binary;
        else if (webhook?.item?.binary) binaryData = webhook.item.binary;
        else if (webhook?.item?.binary?.data) binaryData = webhook.item.binary;
        else if (webhook?.item?.binary?.file) binaryData = { data: webhook.item.binary.file };
    } catch (e) {
        console.error('Fehler beim Holen vom Webhook:', e);
    }
}

// 4. Fallback: $binary
if (!binaryData && $binary) {
    binaryData = $binary;
}

// KRITISCH: Wenn KEINE Binary-Daten ‚Üí RETURN [] statt Error (verhindert 500 Error!)
if (!binaryData || (typeof binaryData === 'object' && Object.keys(binaryData).length === 0)) {
    console.error('Binary-Daten fehlen f√ºr Sample:', sample || 'Upload');
    // RETURN [] statt Error werfen - verhindert 500 Error!
    return [];
}

// RETURN: Item mit Binary
return [{
    json: {
        ...$json,
        sample: sample,
        source: sample ? `Sample ${sample}` : 'Upload'
    },
    binary: binaryData
}];

// Parse AI Agent Response (Vertex AI Gemini)

// Detailliertes Logging
console.log('üîç [Parse AI Agent Response] Starte Parsing');
console.log('‚è∞ Zeitstempel:', new Date().toISOString());
console.log('üìã Input Response:', JSON.stringify($json, null, 2));

const response = $json;
const source = $('Setze Sample-Info').item.json.source || 'Upload';
const sample = $('Setze Sample-Info').item.json.sample || '';

console.log('üìù Metadaten:', { source, sample });

// AI Agent Response Struktur kann variieren:
// Option 1: Direkt JSON Response
// Option 2: Text mit JSON

let extractedData = {};
let responseText = '';

try {
  // Versuche verschiedene Response-Strukturen
  if (response.output) {
    responseText = response.output;
  } else if (response.chatOutput) {
    responseText = response.chatOutput;
  } else if (response.text) {
    responseText = response.text;
  } else if (response.message) {
    responseText = response.message;
  } else if (response.candidates?.[0]?.content?.parts?.[0]?.text) {
    // Vertex AI native Format
    responseText = response.candidates[0].content.parts[0].text;
  } else if (typeof response === 'string') {
    responseText = response;
  } else if (typeof response === 'object' && response.name) {
    // Bereits strukturiertes JSON
    extractedData = response;
  }

  // Extrahiere JSON aus Text (falls zus√§tzlicher Text vorhanden)
  if (responseText) {
    const jsonMatch = responseText.match(/\{[\s\S]*\}/);
    const jsonString = jsonMatch ? jsonMatch[0] : responseText;
    extractedData = JSON.parse(jsonString);
  }
} catch (error) {
  // Fallback: Versuche direkt zu parsen
  if (typeof response === 'object' && !response.candidates) {
    extractedData = response;
  } else {
    console.error('Fehler beim Parsen der AI Agent Response:', error);
    console.error('Response:', JSON.stringify(response, null, 2));
    extractedData = {};
  }
}

// F√ºge Metadaten hinzu
const extractedResult = {
  json: {
    name: extractedData.name || '',
    company: extractedData.company || '',
    email: extractedData.email || '',
    phone: extractedData.phone || extractedData.phone_mobile || '',
    phone_office: extractedData.phone_office || extractedData.phone_b√ºro || '',
    address: extractedData.address || '',
    city: extractedData.city || '',
    website: extractedData.website || extractedData.www || '',
    job_title: extractedData.job_title || extractedData.position || '',
    verification_status: extractedData.verification_status || 'unverified',
    confidence_score: extractedData.confidence_score !== undefined ? extractedData.confidence_score : 0.5,
    source: source,
    sample: sample,
    timestamp: new Date().toISOString()
  }
};

